import scrapy
import logging

logging.getLogger('scrapy').setLevel(logging.WARNING)

class locus(scrapy.Spider):
    name = 'Wikipedia'
    start_urls = ['https://en.wikipedia.org/wiki/Web_crawler']

    def parse(self, response):
        print(
            response.css('h1#firstHeading::text').extract(),
            ''.join(response.css('div#mw-content-text>div>p')[1].css('::text').extract())
        )
        for e in response.css('div#mw-content-text>div>p'):
            yield{'para': ''.join(e.css('::text').extract())}